import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { RestService } from 'src/app/service/rest.service';

@Component({
  selector: 'app-editar-libro',
  templateUrl: './editar-libro.component.html',
  styleUrls: ['./editar-libro.component.css']
})
export class EditarLibroComponent implements OnInit {

  no_disponible = "https://dynamicmediainstitute.org/wp-content/themes/dynamic-media-institute/imagery/default-book.png";
  imagen: any = null;

  libro = {
    nombre: "",
    descripcion: "",
    precio: "",
    imagen: null
  };

  messageOk = null;
  messageErr = null;

  constructor(private rest: RestService, private route: Router) { }

  ngOnInit(): void {
    this.cargarLibro();
  }

  async cargarLibro() {
    var id = sessionStorage.getItem('id');
    var res = await this.rest.GetRequest('listOfBooks/' + id).toPromise();
    res = res.data;
    this.libro.nombre = res.nombre;
    this.libro.descripcion = res.descripcion;
    this.libro.precio = res.precio;
    this.libro.imagen = res.imagen;
    this.imagen = res.imagen;
  }

  uploadImagen(event: any) {
    if (event.target.files && event.target.files[0]) {
      var file = event.target.files[0];
      const reader = new FileReader();
      reader.onload = e => this.imagen = reader.result;
      reader.readAsDataURL(file);
    }
  }

  async actualizar() {
    // obtener imagen
    this.libro.imagen = this.imagen;

    var id = sessionStorage.getItem('id');

    try {
      var res = await this.rest.PutRequest('updateBook/' + id, this.libro).toPromise();
      this.messageOk = res.message;
    } catch (error: any) {
      this.messageErr = error.error.message
    }
  }

  cancelar() {
    this.route.navigate(["libros"])
  }

  cerrarAlert1() {
    this.messageOk = null;
  }

  cerrarAlert2() {
    this.messageErr = null;
  }

}
<div class="bg-light">
  <app-menuuser></app-menuuser>
  <main>
    <section class="jumbotron text-center my-1">
      <div class="container">
        <h1 class="jumbotron-heading text-center">
          Libro nuevo
        </h1>
      </div>
    </section>
    <div class="container">
      <div class="alert alert-success alert-dismissible fade show" role="alert" *ngIf="messageOk != null">
        <strong>{{messageOk}}</strong>
        <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close" (click)="cerrarAlert1()"></button>
      </div>
      <div class="alert alert-danger alert-dismissible fade show" role="alert" *ngIf="messageErr != null">
        <strong>{{messageErr}}</strong>
        <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close" (click)="cerrarAlert2()"></button>
      </div>

      <div class="py-2 d-flex justify-content-center">
        <div>
          <div class="input-group mb-3">
            <img [src]="imagen || no_disponible" class="card-img-top" alt="..." style="max-width: 350px;" height="250">
          </div>
          <div class="input-group mb-3">
            <input type="file" class="form-control" accept="image/*" (change)="uploadImagen($event)">
          </div>
          <div class="form-group mb-3">
            <label for="name">Nombre</label>
            <input class="form-control item" type="name" id="name" name="inputName" placeholder="Nombre" [(ngModel)]="libro.nombre">
          </div>
          <div class="form-group mb-3">
            <label for="descripcion">Descripcion</label>
            <textarea class="form-control" id="descripcion" name="inputDescripcion" placeholder="Descripcion" [(ngModel)]="libro.descripcion"></textarea>
          </div>
          <div class="form-group mb-3">
            <label for="precio">Precio</label>
            <input class="form-control item" type="number" id="precio" name="inputPrecio" placeholder="0.0" [(ngModel)]="libro.precio">
          </div>
          <div class="btn-group mb-3">
            <button type="button" class="me-2 btn btn-outline-primary" (click)="actualizar()">Actualizar</button>
            <button type="button" class="btn btn-outline-danger" (click)="cancelar()">Cancelar</button>
          </div>
        </div>
      </div>
    </div>
  </main>
</div>
