import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { RestService } from 'src/app/service/rest.service';

@Component({
  selector: 'app-album',
  templateUrl: './album.component.html',
  styleUrls: ['./album.component.css']
})
export class AlbumComponent implements OnInit {

  messageOk = null;
  messageErr = null;

  no_disponible = "https://dynamicmediainstitute.org/wp-content/themes/dynamic-media-institute/imagery/default-book.png";

  libros = [
    { nombre: "nombre", descripcion: "descripcion", precio: 20.95, imagen: "https://yarkozzz.files.wordpress.com/2015/03/random_tech_266.jpg" },
    { nombre: "nombre", descripcion: "descripcion", precio: 20.95, imagen: "https://yarkozzz.files.wordpress.com/2015/03/random_tech_266.jpg" },
    { nombre: "nombre", descripcion: "descripcion", precio: 20.95, imagen: "https://yarkozzz.files.wordpress.com/2015/03/random_tech_266.jpg" },
    { nombre: "nombre", descripcion: "descripcion", precio: 20.95, imagen: "https://yarkozzz.files.wordpress.com/2015/03/random_tech_266.jpg" },
    { nombre: "nombre", descripcion: "descripcion", precio: 20.95, imagen: "https://yarkozzz.files.wordpress.com/2015/03/random_tech_266.jpg" },
    { nombre: "nombre", descripcion: "descripcion", precio: 20.95, imagen: "https://yarkozzz.files.wordpress.com/2015/03/random_tech_266.jpg" },
    { nombre: "nombre", descripcion: "descripcion", precio: 20.95, imagen: "https://yarkozzz.files.wordpress.com/2015/03/random_tech_266.jpg" },
    { nombre: "nombre", descripcion: "descripcion", precio: 20.95, imagen: "https://yarkozzz.files.wordpress.com/2015/03/random_tech_266.jpg" },
  ];

  constructor(private rest: RestService, private route: Router) { }

  ngOnInit(): void {
    this.getLibros();
  }

  async getLibros() {
    var res = await this.rest.GetRequest('listOfBooks').toPromise();
    this.libros = res.data;
  }

  async eliminar(i: any) {
    var libro: any = this.libros[i];
    try {
      var res = await this.rest.DeleteRequest('deleteBook/' + libro._id.$oid).toPromise();
      this.messageOk = res.message;
      this.getLibros();
    } catch (error: any) {
      this.messageErr = error.error.message;
    }
  }

  editar(i: any) {
    var libro: any = this.libros[i];
    sessionStorage.setItem('id', libro._id.$oid);
    this.route.navigate(["libroEdit"]);
  }

  cerrarAlert1() {
    this.messageOk = null;
  }

  cerrarAlert2() {
    this.messageErr = null;
  }
}
<div class="bg-light">
  <app-menuuser></app-menuuser>

  <main>
    <section class="jumbotron text-center my-5">
      <div class="container">
        <h1 class="jumbotron-heading">Libros</h1>
      </div>
    </section>

    <div class="container">
      <div class="alert alert-success alert-dismissible fade show" role="alert" *ngIf="messageOk != null">
        <strong>{{messageOk}}</strong>
        <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close" (click)="cerrarAlert1()"></button>
      </div>
      <div class="alert alert-danger alert-dismissible fade show" role="alert" *ngIf="messageErr != null">
        <strong>{{messageErr}}</strong>
        <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close" (click)="cerrarAlert2()"></button>
      </div>
      <div class="py-3">

        <div class="row row-cols-1 row-cols-md-3 g-4">
          <div class="col" *ngFor="let libro of libros; let i = index">
            <div class="card mb-4 box-shadow">
              <img src="{{libro.imagen || no_disponible}}" class="card-img-top" alt="..." height="250" width="100">
              <div class="card-body">
                <h5 class="card-title">{{libro.nombre}}</h5>
                <p class="card-text">{{libro.descripcion}}</p>
                <div class="d-flex justify-content-between align-items-center">
                  <div class="badge bg-secondary text-wrap fs-6">
                    Precio: Q{{libro.precio}}
                  </div>
                  <div class="btn-group">
                    <button type="button" class="me-2 btn btn-outline-primary" (click)="editar(i)">Editar</button>
                    <button type="button" class="btn btn-outline-danger" (click)="eliminar(i)">Eliminar</button>
                  </div>
                </div>
              </div>
            </div>
          </div>

        </div>
      </div>
    </div>
  </main>
</div>
